import React, { useRef, useEffect } from 'react'
import Placeholder from '../Placeholder';
import './style.css';

const VideoPlayer = (show) => {
const videoRef = useRef(null)
const { IVSPlayer } = window;
const [loading, setLoading] = useState(true);

useEffect(() => {
    if(!IVSPlayer.isPlayerSupported) {
        console.warn('The Current browser does not support the Amazon IVS player')
        return;
    }

    const PLAYBACK_URL = 'https://927665d0bc2b.us-east-1.playback.live-video.net/api/video/v1/us-east-1.009965025047.channel.i6wEZhrSC6Wk.m3u8';
    // initialize ivs player
    const player = IVSPlayer.create();
    player.attachHTMLVideoElement(videoRef.current);
    player.load(PLAYBACK_URL);
    player.play();
}, [IVSPlayer])


// if(!IVSPlayer.isPlayerSupported) {
//     console.warn('The Current browser does not support the Amazon IVS player')
//     return;
// }

  if (!show) {
    return null
  }


  return (
      <div className="VideoPlayer">
          <Placeholder loading={loading} />
          <video ref={videoRef} id="video-player" playsInline></video>
      </div>
  );

}

export default VideoPlayer;
